cirCons = [
    [*''],
    [*''],
    [*'']]
# [ halfpi, normpi, tau2pi ]  #list(map(int, if using numpy for advance array dimension control

repColor = ('\033[90m','\033[37m','\033[32m')
rowrap = 100
print('')

for pos, digit in enumerate(cirCons[0]):
    repCount = 0
    lastSame = ''
    saveRep = ''
    for con in cirCons:
        if lastSame == con[pos]:
            repCount += 1
            saveRep = con[pos]
        lastSame = con[pos]
    for count, con in enumerate(cirCons):
        if con[pos] == saveRep: cirCons[count][pos] = repColor[repCount] + con[pos]
        else: cirCons[count][pos] = repColor[0] + con[pos]

while len(cirCons[0]) > 1:
    for count, con in enumerate(cirCons):
        print(*con[0:rowrap-1],sep='')
        cirCons[count] = con[rowrap:]
    print('')
